searchUrl:
/api/fqsearch/books?query={{key}}&offset={{(page-1)*20}}&count=20&tabType=3@js:
var offsetMatch = /(?:^|[?&])offset=(\d+)/.exec(result);
var countMatch = /(?:^|[?&])count=(\d+)/.exec(result);
var offset = offsetMatch ? parseInt(offsetMatch[1], 10) : 0;
var count = countMatch ? parseInt(countMatch[1], 10) : 20;
if (!count || count <= 0) {
    count = 20;
}
var currentPage = Math.floor((isNaN(offset) ? 0 : offset) / count) + 1;
if (currentPage === 1) {
    java.put('search_id', '');
}
var searchId = java.get('search_id');
if (searchId && currentPage > 1) {
    result = result + '&searchId=' + encodeURIComponent(searchId);
}
result;
