searchUrl:
/api/search?key={{key}}&page={{page}}&size=20&tabType=3@js:
var pageMatch = /(?:^|[?&])page=(\d+)/.exec(result);
var currentPage = pageMatch ? parseInt(pageMatch[1], 10) : 1;
if (currentPage === 1) {
    java.put('search_id', '');
}
var searchId = java.get('search_id');
if (searchId && currentPage > 1) {
    result = result + '&searchId=' + encodeURIComponent(searchId);
}
result;
